"use strict";

var button = document.querySelector(".js-hamburger");
var sideNavButton = document.querySelector(".js-close-icon");
var sidenav = document.querySelector(".js-sidenav");
var navItems = document.querySelectorAll('.nav__item'); // Toggle sidenav when hamburger icon has been clicked on

button.addEventListener("click", function () {
  sidenav.classList.toggle("js-sidenav--toggled");
}); // Remove sidenav when close icon has been clicked on

sideNavButton.addEventListener("click", function () {
  sidenav.classList.remove("js-sidenav--toggled");
}); // Remove sidenav when nav items have been clicked on

navItems.forEach(function (element) {
  element.addEventListener('click', function (e) {
    sidenav.classList.remove("js-sidenav--toggled");
  });
});
"use strict";

var excerpts = document.querySelectorAll('.blog__excerpt');
excerpts.forEach(function (excerpt) {
  var text = excerpt.textContent;

  if (text.length > 15) {
    excerpt.textContent = "".concat(text.split(' ').slice(0, 15).join(' ') + '..');
  }
});
